#include <stdio.h>
void main() {
 // 打印菱形
 int i,j,k,h,g;
 int sum = 2;
 // 定义变量layer表示层数
 int layer = 7;
 // 控制总层数
 for(i=1; i<=layer; i++) {
  if(i<=(layer+1)/2) {
   // 上半部分
   for(h=1; h<=layer-i; h++) {
    printf(" ");
   }
   for(j=1; j<=i*2-1; j++) {
    if(j==1 || j==i*2-1) {
     printf("*");
    } else {
     printf(" ");
    }
   }
  } else {
   // 下半部分
   for(g=1; g<=i-1; g++) {
    printf(" ");
   }
   for(k=1; k<=(i*2-1)-(2*sum); k++) {
    if(k==1 || k==(i*2-1)-(2*sum)) {
     printf("*");
    } else {
     printf(" ");
    }
   }
   sum+=2;
  }
  printf("\n");
 }

