day10 飞机按键控制优化，实现长按按键连续移动
def keyControl(airplane_obj):
    event_list = pygame.event.get()

    for event in event_list:
        key_pressed = pygame.key.get_pressed()

        if event.type == QUIT:
            print('退出')
            exit()
        elif key_pressed:

            if key_pressed【pygame.K_a】 or key_pressed【pygame.K_LEFT】:
                print("左移")
                airplane_obj.moveLeft()
            elif key_pressed【pygame.K_d】 or key_pressed【pygame.K_RIGHT】:
                print("右移")
                airplane_obj.moveRight()

            if key_pressed【pygame.K_w】 or key_pressed【pygame.K_UP】:
                print("上移")
                airplane_obj.moveUp()
            elif key_pressed【pygame.K_s】 or key_pressed【pygame.K_DOWN】:
                print("下移")
                airplane_obj.moveDown()

            if key_pressed【pygame.K_SPACE】:
                print("攻击")
                airplane_obj.attack()
